onmessage = function (oEvent) { 
    var tiempo=oEvent.data;
    totalTimeTpv=tiempo;
    updateClockTpv2();
    
};

function updateClockTpv2() {
    if(totalTimeTpv==0){
        postMessage(totalTimeTpv);
    }
    else{
        totalTimeTpv-=1;
        postMessage(totalTimeTpv);
        setTimeout('updateClockTpv2()', 1000);
    }
}
